#!/usr/bin/env bash

# constant shorthand for ANSI escape sequences:

# terminal color style format string
export CS_black="\033[30m"
export CS_red="\033[31m"
export CS_green="\033[32m"
export CS_yellow="\033[33m"
export CS_blue="\033[34m"
export CS_magenta="\033[35m"
export CS_cyan="\033[36m"
export CS_white="\033[37m"

# bright
export CS_bblack="\033[90m"
export CS_bred="\033[91m"
export CS_bgreen="\033[92m"
export CS_byellow="\033[93m"
export CS_bblue="\033[94m"
export CS_bmagenta="\033[95m"
export CS_bcyan="\033[96m"
export CS_bwhite="\033[97m"

# bold (bd)
export CS_black_bd="\033[1;30m"
